mapper.select:
select sum(case when adviceID=0 then amount else 0 end) clinicAmount, sum(case when adviceID>0 then amount else 0 end)hospitalAmount ,
count(distinct case when adviceID=0 then patientID else null end) clinicPatient, count(distinct case when adviceID>0 then patientID else null end) hospitalPatient
from ${DrugRecordsTable} T

    AND T.dispensingDate >= #{fromDate:TIMESTAMP}
    AND T.dispensingDate < #{toDate:TIMESTAMP}

SELECT convert(VARCHAR(7), T.dispensingDate, 120) MONTH,
       sum(T.amount)                              amount,
       sum(T.quantity)                            quantity
FROM ${DrugRecordsTable} T
         LEFT JOIN Medicine M1
                   ON M1.no = T.goodsNo
WHERE T.doctorName = #{doctorName:VARCHAR}
  AND T.dispensingDate > #{fromDate:VARCHAR}
  AND M1.medicineNo = #{medicineNo:VARCHAR}
GROUP BY convert(VARCHAR(7), T.dispensingDate, 120)

SELECT P.*,M.antiClass mAntiClass,D.antiClass dAntiClass ,D.name doctorName FROM Clinic P
LEFT JOIN RxDetail R ON P.clinicID=R.clinicID
LEFT JOIN Medicine M ON R.medicineNo=M.medicineNo
LEFT JOIN doctor D ON P.doctorNo = D.doctorNo WHERE M.antiClass>=1 AND M.antiClass>D.antiClass
AND P.clinicDate >= #{clinicDateFrom:TIMESTAMP}
AND P.clinicDate < #{clinicDateTo:TIMESTAMP}
AND M.antiClass = #{antiClass:INTEGER}
