choose.when:
AND clinicID IN (SELECT DISTINCT R.clinicID FROM ${RxDetailTable} R JOIN medicine M ON R.goodsID = M.goodsID

                        AND M.antiClass >= 0
                        AND M.antiClass = #{antiClass:INTEGER}

                    )

mapper.update:
UPDATE Clinic
publish = publish | #{publish:INTEGER}, result = #{result:VARCHAR}, reviewDate = #{reviewDate:TIMESTAMP}, reviewUser = #{reviewUser:VARCHAR}, disItem = #{disItem:VARCHAR}, rational = #{rational:INTEGER}, appealState = #{appealState:INTEGER},
WHERE clinicID = #{clinicID:INTEGER}

mapper.select:
SELECT P.*,M.antiClass mAntiClass,D.antiClass dAntiClass ,D.name doctorName FROM Clinic P
LEFT JOIN ${RxDetailTable} R ON P.clinicID=R.clinicID
LEFT JOIN Medicine M ON R.goodsID=M.goodsID
LEFT JOIN doctor D ON P.doctorID = D.doctorID WHERE M.antiClass>=1 AND M.antiClass>D.antiClass
AND P.clinicDate >= #{fromDate:TIMESTAMP}
AND P.clinicDate < #{toDate:TIMESTAMP}
AND M.antiClass >0
AND M.antiClass = #{antiClass:INTEGER}

SELECT P.*,M.antiClass mAntiClass,D.antiClass dAntiClass ,D.name doctorName FROM Clinic P
LEFT JOIN ${RxDetailTable} R ON P.clinicID=R.clinicID
LEFT JOIN Medicine M ON R.goodsID=M.goodsID
LEFT JOIN doctor D ON P.doctorID = D.doctorID WHERE M.antiClass=3
AND P.clinicDate >= #{fromDate:TIMESTAMP}
AND P.clinicDate < #{toDate:TIMESTAMP}
