choose.when:
AND C.clinicID IN (SELECT DISTINCT R.clinicID FROM ${RxDetailTable} R JOIN medicine M ON R.goodsID = M.goodsID

                        AND M.antiClass >= 0
                        AND M.antiClass = #{antiClass:INTEGER}

                    )

mapper.select:
select C.clinicID,C.mzNo,C.department,T.name doctorName,M.no, M.ypID, D.goodsID,D.dosage + ' '+D.spec dosage, D.eachQuantity , D.eachUnit ,D.frequency ,D.quantity,D.price,D.money
from (
select TOP ${limit} C.clinicID,C.mzNo,C.department,C.doctorID,C.money clinicMoney from Clinic C

    LEFT JOIN ${RxDetailTable} D ON C.clinicID = D.clinicID


    LEFT JOIN ${RxDetailTable} D2 ON C.clinicID = D2.clinicID


ORDER BY ${orderField} ${orderDir}
) C
left join Doctor T on C.doctorID=T.doctorID
join ${RxDetailTable} D on C.clinicID=D.clinicID
left join Medicine M on D.goodsID=M.goodsID
ORDER BY ${orderField} ${orderDir}

mapper.update:
UPDATE Clinic
publish = publish | #{publish:INTEGER}, result = #{result:VARCHAR}, reviewDate = #{reviewDate:DATE}, reviewUser = #{reviewUser:VARCHAR}, disItem = #{disItem:VARCHAR}, rational = #{rational:INTEGER}, appealState = #{appealState:INTEGER},
WHERE clinicID = #{clinicID:INTEGER}

mapper.select:
SELECT P.*,M.antiClass mAntiClass,D.antiClass dAntiClass ,D.name doctorName FROM Clinic P
LEFT JOIN ${RxDetailTable} R ON P.clinicID=R.clinicID
LEFT JOIN Medicine M ON R.goodsID=M.goodsID
LEFT JOIN doctor D ON P.doctorID = D.doctorID WHERE M.antiClass>=1 AND M.antiClass>D.antiClass
AND P.clinicDate >= #{fromDate:DATE}
AND P.clinicDate < #{toDate:DATE}
AND M.antiClass >0
AND M.antiClass = #{antiClass:INTEGER}

SELECT P.*,M.antiClass mAntiClass,D.antiClass dAntiClass ,D.name doctorName FROM Clinic P
LEFT JOIN ${RxDetailTable} R ON P.clinicID=R.clinicID
LEFT JOIN Medicine M ON R.goodsID=M.goodsID
LEFT JOIN doctor D ON P.doctorID = D.doctorID WHERE M.antiClass=3
AND P.clinicDate >= #{fromDate:DATE}
AND P.clinicDate < #{toDate:DATE}
