mapper.select:
SELECT
contagionID,objectType,patientID,hospID,department,patientName,idNo,boy,age,inDate,hospitalDay,baseDiagnosis1,baseDiagnosis2,baseDiagnosis3,baseDiagnosis4,hospitalInfect1,occurDay1,
hospitalInfect2,occurDay2,factor,surgeryName,emergency,surgeryDate,surgeryDoctor,lastTime1,lastTime2,anaesthesia,cut,feature,microbe,time1,time2,time3,drainage,hormone,time4,time5,
sample,bAntiDrug,aAntiDrug,pAntiDrug,antiItem,masterDoctor,monitorDoctor,reportTime,workflow,doctorUserID,doctorUsername,workflowNote FROM Contagion

    AND reportTime >= #{fromDate:TIMESTAMP}
    AND reportTime < #{toDate:TIMESTAMP}
    AND doctorUserID = #{doctorUserID:INTEGER}
    AND doctorUsername = #{doctorUsername:VARCHAR}
    AND patientName = #{queryField:VARCHAR}
    AND workflow%10 = 3
    AND workflow%10 != 3
    AND workflow % 10 != #{notWorkflow:INTEGER}

order by contagionID desc

SELECT
infectiousID,objectType,patientID,hospID,reportNo,reportType,patientName,patientParent,idCardNo,boy,birthday,age,ageUnit,workplace,linkPhone,belongTo,address,occupation,occupationElse,caseClass,
accidentDate,diagnosisDate,diagnosisHour,deathDate,infectiousClass,infectiousName,correctName,cancelCause,reportUnit,doctorPhone,reportDoctor,memo,
marital,nation,nationElse,education,venerismHis, registerAddr,touchHis,touchElse,infectRoute,sampleSource,conclusion,checkConfirmDate,hivConfirmDate,checkUnit,
convert(varchar(16),fillTime,120) fillTime,workflow,doctorUserID,doctorUsername,workflowNote
FROM Infectious

    AND fillTime >= #{fromDate:TIMESTAMP}
    AND fillTime < #{toDate:TIMESTAMP}


            AND infectiousName LIKE '%${queryField}%'
            AND patientName = #{queryField:VARCHAR}


    AND doctorUserID = #{doctorUserID:INTEGER}
    AND doctorUsername = #{doctorUsername:VARCHAR}
    AND workflow%10 = 3
    AND workflow%10 != 3
    AND workflow % 10 != #{notWorkflow:INTEGER}

order by updateTime desc

mapper.update:
UPDATE Infectious
reportNo = #{reportNo:VARCHAR}, patientID = #{patientID:INTEGER}, hospID = #{hospID:VARCHAR}, reportType = #{reportType:INTEGER}, patientName = #{patientName:VARCHAR}, patientParent = #{patientParent:VARCHAR}, idCardNo = #{idCardNo:VARCHAR}, boy = #{boy:VARCHAR}, birthday = #{birthday:VARCHAR}, age = #{age:VARCHAR}, ageUnit = #{ageUnit:VARCHAR}, workplace = #{workplace:VARCHAR}, linkPhone = #{linkPhone:VARCHAR}, belongTo = #{belongTo:VARCHAR}, address = #{address:VARCHAR}, occupation = #{occupation:INTEGER}, occupationElse = #{occupationElse:VARCHAR}, caseClass = #{caseClass:INTEGER}, accidentDate = #{accidentDate:VARCHAR}, diagnosisDate = #{diagnosisDate:VARCHAR}, diagnosisHour = #{diagnosisHour:VARCHAR}, deathDate = #{deathDate:VARCHAR}, infectiousClass = #{infectiousClass:INTEGER}, infectiousName = #{infectiousName:VARCHAR}, correctName = #{correctName:VARCHAR}, cancelCause = #{cancelCause:VARCHAR}, reportUnit = #{reportUnit:VARCHAR}, doctorPhone = #{doctorPhone:VARCHAR}, reportDoctor = #{reportDoctor:VARCHAR}, memo = #{memo:VARCHAR}, marital = #{marital:INTEGER}, nation = #{nation:INTEGER}, nationElse = #{nationElse:VARCHAR}, education = #{education:VARCHAR}, venerismHis = #{venerismHis:VARCHAR}, registerAddr = #{registerAddr:VARCHAR}, touchHis = #{touchHis:INTEGER}, touchElse = #{touchElse:VARCHAR}, infectRoute = #{infectRoute:VARCHAR}, sampleSource = #{sampleSource:VARCHAR}, conclusion = #{conclusion:VARCHAR}, checkConfirmDate = #{checkConfirmDate:VARCHAR}, hivConfirmDate = #{hivConfirmDate:VARCHAR}, checkUnit = #{checkUnit:VARCHAR}, workflow = #{workflow:INTEGER}, workflowNote = #{workflowNote:VARCHAR}, updateTime=getdate()
WHERE infectiousID=#{infectiousID:INTEGER}

UPDATE Contagion
patientID = #{patientID:INTEGER}, hospID = #{hospID:VARCHAR}, department = #{department:VARCHAR}, patientName = #{patientName:VARCHAR}, idNo = #{idNo:VARCHAR}, boy = #{boy:INTEGER}, age = #{age:VARCHAR}, inDate = #{inDate:VARCHAR}, hospitalDay = #{hospitalDay:VARCHAR}, baseDiagnosis1 = #{baseDiagnosis1:VARCHAR}, baseDiagnosis2 = #{baseDiagnosis2:VARCHAR}, baseDiagnosis3 = #{baseDiagnosis3:VARCHAR}, baseDiagnosis4 = #{baseDiagnosis4:VARCHAR}, hospitalInfect1 = #{hospitalInfect1:VARCHAR}, occurDay1 = #{occurDay1:VARCHAR}, hospitalInfect2 = #{hospitalInfect2:VARCHAR}, occurDay2 = #{occurDay2:VARCHAR}, factor = #{factor:VARCHAR}, surgeryName = #{surgeryName:VARCHAR}, emergency = #{emergency:INTEGER}, surgeryDate = #{surgeryDate:VARCHAR}, surgeryDoctor = #{surgeryDoctor:VARCHAR}, lastTime1 = #{lastTime1:VARCHAR}, lastTime2 = #{lastTime2:VARCHAR}, anaesthesia = #{anaesthesia:INTEGER}, cut = #{cut:INTEGER}, feature = #{feature:INTEGER}, microbe = #{microbe:VARCHAR}, time1 = #{time1:VARCHAR}, time2 = #{time2:VARCHAR}, time3 = #{time3:VARCHAR}, drainage = #{drainage:VARCHAR}, hormone = #{hormone:VARCHAR}, time4 = #{time4:VARCHAR}, time5 = #{time5:VARCHAR}, sample = #{sample:VARCHAR}, bAntiDrug = #{bAntiDrug:VARCHAR}, aAntiDrug = #{aAntiDrug:VARCHAR}, pAntiDrug = #{pAntiDrug:VARCHAR}, antiItem = #{antiItem:INTEGER}, masterDoctor = #{masterDoctor:VARCHAR}, monitorDoctor = #{monitorDoctor:VARCHAR}, workflow = #{workflow:INTEGER}, workflowNote = #{workflowNote:VARCHAR},
WHERE contagionID=#{contagionID:INTEGER}
