mapper.select:
SELECT TOP ${limit} clinicID objectID, clinicType, serialNo, serialNo patientNo, clinicDate date, department, doctorName, reviewDate, reviewUser, rational, result reviewResult, drugNum ,money,antiNum,antiMoney,patientName,appealState FROM Clinic where reviewDate > '2010-01-01' and doctorName=#{doctorName:VARCHAR}
and reviewDate < (SELECT min(reviewDate) FROM (SELECT TOP ${start} reviewDate FROM Clinic where reviewDate > '2010-01-01' and doctorName=#{doctorName:VARCHAR}ORDER BY reviewDate DESC ) A )
ORDER BY reviewDate DESC

SELECT TOP ${limit} recipeID objectID, R.serialNo, R.patientNo, R.outDate date, R.department, R.masterDoctorName doctorName, R.reviewDate reviewDate, R.reviewUser, R.rational, V.review reviewResult,R.drugNum,R.money,R.antiNum,R.antiMoney,R.patientName,R.appealState FROM Recipe R LEFT JOIN RecipeReview V ON R.serialNo = V.serialNo WHERE R.reviewDate > '2010-01-01' and masterDoctorName=#{doctorName:VARCHAR}
and R.reviewDate < (SELECT min(reviewDate) FROM (SELECT TOP ${start} reviewDate FROM Recipe where reviewDate > '2010-01-01' and masterDoctorName=#{doctorName:VARCHAR} ORDER BY reviewDate DESC ) A )
ORDER BY reviewDate DESC
